const String main_page_html = "<!DOCTYPE html><html><head><meta name=\"viewport\"content=\"width=device-width,initial-scale=1,user-scalable=no\"><title>Coin Ticker</title><style>b*{box-sizing:border-box}body{background-color:#f1f1f1}#regForm{background-color:#fff;margin:100px auto;font-family:Raleway;padding:40px;width:70%;min-width:300px}h1{text-align:center}input{padding:10px;width:100%;font-size:17px;font-family:Raleway;border:1px solid #aaa}input.invalid{background-color:#fdd}.tab{display:none}button{background-color:#4caf50;color:#fff;border:none;padding:10px 20px;font-size:17px;font-family:Raleway;cursor:pointer}button:hover{opacity:.8}#prevBtn{background-color:#bbb}.step{height:15px;width:15px;margin:0 2px;background-color:#bbb;border:none;border-radius:50%;display:inline-block;opacity:.5}.step.active{opacity:1}.step.finish{background-color:#4caf50}</style></head><body><form id=\"regForm\"onsubmit=\"event.preventDefault()\"><h1>Configure Crypto Value Displayer</h1><div class=\"tab\"id=\"tab1\">Connect to Wi-Fi:<p><input placeholder=\"SSID\"oninput='this.className=\"\"'name=\"ssid\"id=\"ssid_data\"></p><p><input placeholder=\"Password\"type=\"password\"oninput='this.className=\"\"'name=\"password\"id=\"password_data\"></p><button type=\"button\"onclick=\"submitData()\">Submit</button></div><div class=\"tab\"id=\"tab2\">Configure Cryptocurrencies:<p><input placeholder=\"E-mail...\"oninput='this.className=\"\"'name=\"email\"></p><p><input placeholder=\"Phone...\"oninput='this.className=\"\"'name=\"phone\"></p></div><div style=\"overflow:auto\"><div style=\"float:right\"></div></div><div style=\"text-align:center;margin-top:40px\"><span class=\"step\"onclick=\"goTo(1)\"></span> <span class=\"step\"onclick=\"goTo(2)\"></span></div><p>Status: <span id=\"device_status\">Establishing Connection...</span></p></form><script>var currentTab=1;function showTab(e){document.getElementById(`tab${e}`).style.display=\"block\",fixStepIndicator(e)}function goTo(e){document.getElementById(`tab${currentTab}`).style.display=\"none\",showTab(currentTab=e)}function nextPrev(e){var t=document.getElementsByClassName(\"tab\");return!(1==e&&!validateForm())&&(t[currentTab].style.display=\"none\",(currentTab+=e)>=t.length?(document.getElementById(\"regForm\").submit(),!1):void showTab(currentTab))}function fixStepIndicator(e){for(var t=document.getElementsByClassName(\"step\"),a=0;a<t.length;a++)t[a].className=t[a].className.replace(\" active\",\"\");t[e].className+=\" active\"}function getData(){var e=new XMLHttpRequest;e.onreadystatechange=function(){4==this.readyState&&200==this.status&&(document.getElementById(\"device_status\").innerHTML=map_status(this.responseText))},e.open(\"GET\",\"status\",!0),e.send()}function submitData(){console.log(\"cenas\");var e=new XMLHttpRequest;e.open(\"POST\",\"submit_data\",!0),e.setRequestHeader(\"Content-Type\",\"application/x-www-form-urlencoded\"),e.onreadystatechange=function(){4==this.readyState&&200==this.status&&(document.getElementById(\"ssid_data\").value=\"\",document.getElementById(\"password_data\").value=\"\"),document.getElementById(\"device_status\").innerHTML=map_status(\"CONNECTING\")},e.send(`ssid_data=${document.getElementById(\"ssid_data\").value}&pass_data=${document.getElementById(\"password_data\").value}`)}function map_status(e){switch(e){case\"CONNECTION_FAILED_STATUS\":return\"Connection could not be established. Please try again.\";case\"STARTING\":return\"Starting the crypto value displayer.\";case\"WAITING_FOR_WIFI_CONNECTION\":return\"Please provide the wifi connection information.\";case\"CONNECTING\":return\"Attempting to connect to your wifi connection. Please wait.\";case\"WIFI_CONNECTED\":return\"Wifi connection was established successfully.\";default:return e}}showTab(currentTab),setInterval(function(){getData()},2e3)</script></body></html>";